import '../css/MainPage.css'
import Ingredients from './Ingredients'
import Receipe from './Receipes'
import React from 'react'
export default function MainPage(){
    const [addIngredients,setaddIngredients]= React.useState([])
    const [receipeShown,setReceipeShown]=React.useState(false)
    const addItem=addIngredients.map((ingredient)=>{
        return(
            <li key={ingredient}>{ingredient}</li>
        )
    })
    
    function form(FormData){
        const newIngredient=FormData.get("ingredient")
        setaddIngredients(addIngredients=>{
            return([...addIngredients, newIngredient])
        })
    }

    function toggleReceipeShown(){
        setReceipeShown(receipeShown=>!receipeShown)
    }
    return(
        <>  
            <div className="mainPage">
                <form className="add_items" action={form}>
                    <input type="text" placeholder='e.g..Oregano' aria-label='Add Ingredient' name="ingredient"/>
                    <button ><span>+</span><p>Add ingredient</p></button>
                </form>
                <Ingredients addItem={addIngredients} toggleReceipeShown={toggleReceipeShown}/>
                {receipeShown && <Receipe/>}
            </div> 
        </>
    )
}
