import '../css/Ingredients.css'
import React from 'react'
export default function Ingredients(props){
    const menu=props.addIngredients.map(ingredient=>{
        return(
        <li>{ingredient}</li>
        )
    })
    return(
        <div className="mainingredient">
            <div className="ingredients_list">
                <div className="ingredients">
                    {menu.length>0 && <h2>Ingredients on Hand:</h2>}
                    <ul className="list">{props.addItem}</ul>
                </div>
            </div>

            {props.addIngredients.length>2 && <div className="ready_receipe">
                <div className="ready_description">
                    <h3>Ready for a receipe? </h3>
                    <p>Generate a receipe from your list of ingredients</p>
                </div>
                <button onClick={props.toggleReceipeShown}>Get a receipe</button>
            </div>}
        </div>
    )
}
