import Letters from './components/pages/letters'
import Display from './components/pages/display'
import './components/css/App.css'
import {useState} from 'react'
export default function App(){
    const [alphabet,setalphabet]=useState(()=>generateAlphabets())
    function generateAlphabets(){
        let alphabets = 'abcdefghijklmnopqrstuvwxyz'
        let resarr=[]
        for(let i=1;i<=26;i++){
            resarr.push({
                value: alphabets[i-1],
                isClick: false,
                isCorrect: true,
                index: i
            })
        }
        return resarr
    }
    function update(id){
        setalphabet(alphabet=>alphabet.map(one=>{
            return one.index===id && one.isClick?{...one,isClick:!one.isClick}:one
        }))
    }
    const arrElements= alphabet.map(each=>{
        return <Letters value={each.value} isClick={each.isClick} isCorrect={each.isCorrect} update={()=>update(each.index)}/>
    })
    return(
        <>
            <div className="section">
                <div className="book">
                    <div className="description">
                        <h1>Assembly Endgame</h1>
                        <h3>Guess the Word under 8 attempts</h3>
                    </div>
                    <div className="display">
                        <Display />
                    </div>
                    <div className="main">
                        {arrElements}
                    </div>
                </div>
                
            </div>
        </>
    )
}
