import Letters from './components/pages/letters'
import './components/css/App.css'
import {useState} from 'react'
export default function App(){
    const [alphabet,setalphabet]=useState(()=>generateAlphabets())
    function generateAlphabets(){
        let alphabets = 'abcdefghijklmnopqrstuvwxyz'
        let resarr=[]
        for(let i=1;i<=26;i++){
            resarr.push({
                value: alphabets[i-1],
                isClick: false,
                isCorrect: true,
                index: i
            })
        }
        return resarr
    }
    function update(id){
        setalphabet()
    }
    const arrElements= alphabet.map(each=>{
        return <Letters value={each.value} isClick={each.isClick} isCorrect={each.isCorrect} update={()=>update(each.index)}/>
    })
    return(
        <>
            <div className="section">
                <div className="main">
                    {arrElements}
                </div>
            </div>
        </>
    )
}
