/**
 * MainPage component
 * 
 * This component represents the main page of the application where users can add ingredients and view a recipe.
 * 
 * @component
 * @returns {JSX.Element} The rendered component.
 * 
 * @example
 * return (
 *   <MainPage />
 * )
 * 
 * @function
 * @name MainPage
 * 
 * @description
 * - Manages the state of added ingredients and whether the recipe is shown.
 * - Provides a form for adding ingredients.
 * - Displays a warning message if the input is invalid.
 * - Toggles the visibility of the recipe.
 * 
 * @property {Array} addIngredients - State to store the list of added ingredients.
 * @property {Function} setaddIngredients - Function to update the addIngredients state.
 * @property {Boolean} receipeShown - State to determine if the recipe is shown.
 * @property {Function} setReceipeShown - Function to update the receipeShown state.
 * 
 * @function toggleVisiblity
 * @description Toggles the visibility of the warning message.
 * @param {string} text - The text to display in the warning message.
 * 
 * @function form
 * @description Handles the form submission to add a new ingredient.
 * @param {FormData} FormData - The form data containing the new ingredient.
 * 
 * @function toggleReceipeShown
 * @description Toggles the visibility of the recipe.
 * 
 * @returns {JSX.Element}
 * - Renders the main page with a form to add ingredients.
 * - Displays the list of added ingredients.
 * - Shows the recipe if the receipeShown state is true.
 */



export default function MainPage(){
    const [addIngredients,setaddIngredients]= React.useState([])
    const [receipeShown,setReceipeShown]=React.useState(false)
    function toggleVisiblity(text){
        const visibleIngredient=document.querySelector(".alert")
        if (visibleIngredient.style.display==="none" || visibleIngredient.style.display===""){
            visibleIngredient.innerText=text ;
            visibleIngredient.style.display="block"
            visibleIngredient.style.opacity="1"
            setTimeout(()=>{visibleIngredient.style.display="none"},3000)
        }


    }
    function form(FormData){
        const newIngredient=FormData.get("ingredient")
        if (newIngredient.trim()==="")
            toggleVisiblity("Warning! Please enter an ingredient")
        else if (addIngredients.includes(newIngredient.toLowerCase().trim()))
            toggleVisiblity("Warning! You already added this ingredient")
        else
        setaddIngredients(addIngredients=>{
            return([...addIngredients, newIngredient.toLowerCase().trim()])
        })
    }

    function toggleReceipeShown(){
        setReceipeShown(receipeShown=>!receipeShown)
    }
    console.log(receipeShown)
    return(
        <>  
            <div className="mainPage">
                <div className="alert warning"> 
                    <strong>Warning!</strong> Please enter an ingredient
                </div>
                <form className="add_items" action={form}>
                    <input type="text" placeholder='e.g..Oregano' aria-label='Add Ingredient' name="ingredient"/>
                    <button ><span>+</span><p>Add ingredient</p></button>
                </form>
                {addIngredients.length>0 && <Ingredients receipeShown={receipeShown} addIngredients={addIngredients} toggleReceipeShown={toggleReceipeShown}/>}
                {receipeShown && <Receipe />}
            </div> 
        </>
    )
}
