import Die from './components/pages/Die'
import {useState,useRef} from 'react'
import Confetti from 'react-confetti'
import './components/css/App.css'
export default function App() {
    
    let GameWon=false
    const [dies,setDies]=useState(()=>generateDies())
    let buttonRef=use
    const DiesElements=dies.map((numdie,idx)=>{
        return <Die value={numdie.value} key={idx} update={()=>update(idx+1)} on={numdie.isHeld}/>
    })

    if(dies.every(die=>die.isHeld) && dies.every(die=> die.value===dies[0].value)){
        GameWon=true
    }

    function generateDies(){
        let dies=[]
        for(let i=1;i<=10;i++){
            dies.push({
                value:Math.ceil(Math.random()*6),
                isHeld: false,
                index:i
            })
        }
        return dies
    }
    function update(id){
        // dies.map((num,idx)=>{
        //     if (idx==id)
        //         setDies({...num[idx],isHeld:true})
        // })
        setDies(oldDice=>(oldDice.map(die=>die.index===id && die.isHeld===false?{...die,isHeld:!die.isHeld}:die)))
        console.log(id)
    }

    function updateDies(){
        !GameWon?setDies(diescheck=>(diescheck.map(choose=>choose.isHeld?choose:{...choose, value:Math.ceil(Math.random()*6)}))):setDies(generateDies())
    }
    return (
        <>
            <div className="main">
                {GameWon && <Confetti />}
                {DiesElements}
                <button className='Roll' onClick={updateDies}>{GameWon?"New Game":"Roll"}</button>
            </div>
        </>
        
    )
}
