import Letters from './components/pages/letters'
import Display from './components/pages/display'
import './components/css/App.css'
import {useState} from 'react'
export default function App(){
    const guessed="react"
    const [alphabet,setalphabet]=useState(()=>generateAlphabets())
    const [available,setavailable]=useState([])
    function generateAlphabets(){
        let alphabets = 'abcdefghijklmnopqrstuvwxyz'
        let resarr=[]
        for(let i=1;i<=26;i++){
            resarr.push({
                value: alphabets[i-1].toUpperCase(),
                isClick: false,
                isCorrect: true,
                index: i
            })
        }
        return resarr
    }
    // function update(id){
    //     setalphabet(alphabet=>alphabet.map(one=>{
    //         return one.index===id && !one.isClick?{...one,isClick:!one.isClick}:one
    //     }))
    // }

    function update(id){
        setalphabet(alphabet=>alphabet.map(each=>{
            if(each.index===id && !each.isClick){
                if(guessed.toUpperCase().includes(each.value)){
                    updateDisplay(each.value)
                    return {...each,isClick:!each.isClick,isCorrect:each.isCorrect}
                }
                else{
                    return {...each,isClick:!each.isClick,isCorrect:!each.isCorrect}
                }
            }
            else{
                return each
            }
        }))
    }

    function updateDisplay(character){
        setavailable(available=>[...available,character])
    }
    const arrElements= alphabet.map(each=>{
        return <Letters value={each.value} isClick={each.isClick} isCorrect={each.isCorrect} update={()=>update(each.index)}/>
    })
    const displayElements= guessed.split('').map(ar=>{
        console.log(available)
        return <Display value={available.includes(ar)?ar.toUpperCase():""} />
    })
    return(
        <>
            <div className="section">
                <div className="book">
                    <div className="description">
                        <h1>Assembly Endgame</h1>
                        <h3>Guess the Word under 8 attempts</h3>
                    </div>
                    <div className="display">
                        {displayElements}
                    </div>
                    <div className="main">
                        {arrElements}
                    </div>
                </div>
                
            </div>
        </>
    )
}
