import Die from './components/pages/Die'
import {useState} from 'react'
import './components/css/App.css'
export default function App() {
    function generateDies(){
        let dies=[]
        for(let i=1;i<=10;i++){
            dies.push({
                value:Math.ceil(Math.random()*6),
                isHeld: false,
                index:i
            })
        }
        console.log(dies)
        return dies
    }
    
    function updateDies(dies){
        dies.map(choose=>{
                return choose.isHeld?choose:{...choose, value:Math.ceil(Math.random()*6)}
            })
    }
    
    const [dies,setDies]=useState(generateDies())
    const DiesElements=dies.map((numdie,idx)=>{
        return <Die value={numdie.value} key={idx} update={()=>update(idx+1)} on={numdie.isHeld}/>
    })
    function changeDice(){
        setDies(updateDies)
    }
    function update(id){
        // dies.map((num,idx)=>{
        //     if (idx==id)
        //         setDies({...num[idx],isHeld:true})
        // })
        setDies(oldDice=>(oldDice.map(die=>die.index===id && die.isHeld===false?{...die,isHeld:!die.isHeld}:die)))
        console.log(id)
    }
    return (
        <>
            <div className="main">
                {DiesElements}
                <button className='Roll' onClick={changeDice}>Roll</button>
            </div>
        </>
        
    )
}
