import Die from './components/pages/Die'
import {useState} from 'react'
import './components/css/App.css'
export default function App() {
    function generateDies(){
        let dies=[]
        for(let i=1;i<=10;i++){
            dies.push({
                value:Math.ceil(Math.random()*6),
                isHeld: True,
            })
        }
        console.log(dies)
        return dies
    }
    
    
    const [dies,setDies]=useState(generateDies())
    const DiesElements=dies.map((numdie,idx)=>{
        return <Die value={numdie.value} key={idx} check={idx+1} update={update} on={numdie.isHeld}/>
    })
    function changeDice(){
        setDies(generateDies)
    }
    function update(id){
        console.log(id)
        // setDies(dies.map((num,idx)=>{
        //     if(idx+1===id){
        //         {..Dies,isHeld:True}
        //     }
        // }))
    }
    return (
        <>
            <div className="main">
                {DiesElements}
                <button className='Roll' onClick={changeDice}>Roll</button>
            </div>
        </>
        
    )
}
