import '../css/MainPage.css'
import React from 'react'
export default function MainPage(){
    const [addIngredients,setaddIngredients]= React.useState(["Tomoatoes","Chicken","Oregano"])
    const addItem=addIngredients.map((each)=>{
        return(
            <li key={each}>{each}</li>
        )
    })
    
    function form(FormData){
        const newIngredient=FormData.get("ingredient")
        setaddIngredients(addIngredients=>{
            return([...addIngredients, newIngredient])
        })
    }

    return(
        <>  
            <div className="mainPage">
                <form className="add_items" action={form}>
                    <input type="text" placeholder='e.g..Oregano' aria-label='Add Ingredient' name="ingredient"/>
                    <button ><span>+</span><p>Add ingredient</p></button>
                </form>
                <div className="ingredients_list">
                    <div className="ingredients">
                        <h2>Ingredients on Hand:</h2>
                        <ul className="list">{addItem}</ul>
                        <div className="ready_receipe">
                    </div>
                    <div className="ready_description">
                        <h2>Ready for a receipe? </h2>
                        </div>
                        <button>Get a receipe</button>
                    </div>
                </div>
            </div> 
        </>
    )
}
