import Die from './components/pages/Die'
import {useState} from 'react'
import './components/css/App.css'
export default function App() {
    function generateDies(){
        let dies=[]
        for(let i=0;i<=9;i++){
            dies.push({
                value:Math.ceil(Math.random()*6),
                isHeld: false}
            )
        }
        console.log(dies)
        return dies
    }
    
    const [dies,setDies]=useState(generateDies())
    const DiesElements=dies.map((numdie)=>{
        return <Die value={numdie}/>
    })
    function changeDice(){
        setDies(generateDies)
    }
    return (
        <>
            <div className="main">
                JSON.stringfy({DiesElements}
                <button className='Roll' onClick={changeDice}>Roll</button>
            </div>
        </>
        
    )
}
